    demoTestFunction: function() {

        var userid = this.getParameter('sysparm_user');

        var gr = new GlideRecord('sys_user');

        gr.addQuery('sys_id',userid);

        gr.query();

        if(gr.next())
            {
                var a = gr.manager.getDisplayValue();

                return a;
            }
    },
    type: 'displayManagerName'
});
